import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { Store } from '@ngxs/store';
import { CatalogueService } from '../catalogue/catalogue.service';
import { Produit } from '../shared/models/produit';
import { AddPanier } from '../shared/actions/panier-action';
import { provideProtractorTestingSupport } from '@angular/platform-browser';

@Component({
  selector: 'app-produits',
  templateUrl: './produits.component.html',
  styleUrls: ['./produits.component.css'],
  providers: [CatalogueService],
})
export class ProduitsComponent implements OnInit {
  recherche: string = '';
  produits$: Observable<Produit[]>;

  constructor(private catalogueService: CatalogueService, private store: Store) {
    this.produits$ = this.catalogueService.getProduits();
  }

  ngOnInit() {}

  addPanier(p:Produit) {
    function showMessage() {

      const messageContainer = document.getElementById("MessageAdd") as HTMLDivElement;
      messageContainer.innerText = `'${p.name}' a été ajouté au panier.`;
      setTimeout(() => {
          messageContainer.innerText = "";
      }, 3000);}


    this.store.dispatch(new AddPanier(p));

    showMessage();

  }  
}
