import { Component, OnInit } from '@angular/core';
import { Select, Store } from '@ngxs/store';
import { Produit } from '../shared/models/produit';
import { DelPanier } from '../shared/actions/panier-action';
import { PanierState } from '../shared/states/panier-state';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-panier',
  templateUrl: './panier.component.html',
  styleUrls: ['./panier.component.css']
})
export class PanierComponent implements OnInit{
  constructor(private store: Store) {}

  @Select(PanierState.getListeProduits) liste$!: Observable<Produit[]>;
  ngOnInit() {}

  delPanier(p: Produit): void {
    function showMessage() {

      const messageContainer = document.getElementById("MessageDelete") as HTMLDivElement;
      messageContainer.innerText = `'${p.name}' a été supprimé du panier.`;
      setTimeout(() => {
          messageContainer.innerText = "";
      }, 3000);
  }
    this.store.dispatch(new DelPanier(p));
    showMessage();
  }
}
<div class="cart-title" >Votre panier <i class="fas fa-shopping-cart"></i> : </div> 

<div id="MessageDelete"></div>
<div id="catalogue" *ngFor="let p of liste$ | async">
  <!-- {{ p.ref }} -->
  {{ p.name }}
  {{ p.price | currency: 'EUR'}}
  <!-- {{ p.qte }} -->
  <button (click)="delPanier(p)"> Supprimer </button>
</div>
