import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { Store } from '@ngxs/store';
import { CatalogService } from '../catalogue/catalog.service';
import { Product } from '../shared/models/product';
import { AddCart } from '../shared/actions/cart-action';
import { provideProtractorTestingSupport } from '@angular/platform-browser';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css'],
  providers: [CatalogService],
})
export class ProductsComponent implements OnInit {
  recherche: string = '';
  products$: Observable<Product[]>;

  constructor(private catalogService: CatalogService, private store: Store) {
    this.products$ = this.catalogService.getProducts();
  }

  ngOnInit() {}

  addCart(p:Product) {
    function showMessage() {

      const messageContainer = document.getElementById("MessageAdd") as HTMLDivElement;
      messageContainer.innerText = `'${p.name}' a été ajouté au panier.`;
      setTimeout(() => {
          messageContainer.innerText = "";
      }, 3000);}


    this.store.dispatch(new AddCart(p));

    showMessage();

  }  
}

<br />
<label for="recherche">Recherche :</label
><input [(ngModel)]="recherche" name="recherche" />

<br />
<br />

<hr />
<br />

<div id="MessageAdd"></div>
<div id="nbProduct"></div>

<div id="container" *ngIf="products$ | async as produits">
  <div id="catalog" *ngFor="let p of products | filter: recherche">
    {{ p.name }}
    <div id="category">{{p.category}}</div>
    <div><button (click)="addCart(p)"> Ajouter au panier ({{ p.price | currency: 'EUR'}})</button></div>
  </div>

</div>
